class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each(function(){t.deleteComment($(this))})}createComment(e){let t=this;this.newCommentForm.submit(function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let s=t.newCommentDom(n.data.comment);$(`#post-comments-${e}`).prepend(s),t.deleteComment($(" .delete-comment-button",s)),new ToggleLike($(" .toggle-like-button",s)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})})}newCommentDom(e){return $(`<li id="comment-${e._id}">\n        <p>\n            <div class="post-details comment-details">\n                <img src="${e.user.avatar}" alt="${e.user.name}" width="40">\n\n                <small id="user-name">\n                      ${e.user.name}\n                </small>\n\n                <small id="likes">\n\n                <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Post">\n                    <span>\n                       <i class="far fa-thumbs-up"></i>   0 Likes\n                     <span>\n                </a>\n               </small>\n                        <small>\n                                <a class="delete-comment-button" href="/comments/destroy/${e._id}">X</a>\n                        </small>\n\n                </div>\n                <br>\n                <div class="user-posts user-comments">\n                      ${e.content}\n                </div>     \n        </p>\n    </li>`)}deleteComment(e){$(e).click(function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})})}}