class ToggleFriend{constructor(e){this.toggler=e,this.toggleFriend()}toggleFriend(){$(this.toggler).click(function(e){e.preventDefault();let t=this;$.ajax({type:"POST",url:$(t).attr("href")}).done(function(e){1==e.data.deleted?$(t).html(" <a> <span>Add Friend </span> </a>"):$(t).html("<a> <span> Remove Friend </span> </a>")}).fail(function(e){console.log("error in completing the request")})})}}